CREATE TABLE IF NOT EXISTS demandes_intervention (id INTEGER AUTO_INCREMENT PRIMARY KEY, statut VARCHAR(50) DEFAULT 'EN_ATTENTE', type_demande VARCHAR(50) NOT NULL DEFAULT 'INTERVENTIONS', product_id BIGINT, produit_nom VARCHAR(255), produit_sn VARCHAR(100), produit_uid VARCHAR(100), produit_fabricant VARCHAR(255), produit_category VARCHAR(100), produit_subcategory VARCHAR(100), produit_description TEXT, type_proprietaire VARCHAR(50), proprietaire_id BIGINT, demande_creation_proprietaire_id BIGINT, proprietaire_nom_temp VARCHAR(255), proprietaire_details_temp TEXT, panne_description TEXT, client_note TEXT, detecteur VARCHAR(255), detector_societe_id BIGINT, demandeur_nom VARCHAR(255), date_demande TIMESTAMP DEFAULT CURRENT_TIMESTAMP, date_creation TIMESTAMP DEFAULT CURRENT_TIMESTAMP, date_modification TIMESTAMP DEFAULT CURRENT_TIMESTAMP);
